import "./steps.css"
import stepsApi from "./../Api/stepsApi"
import {useState} from "react"
import Step from "./Step";

const Steps = () => {

    const [steps,setSteps] = useState(stepsApi)


    return(
        <div className="container">
                <div className="row">

                    <div className="col-sm-4 col-md-3 side-content">
                        <div className="bs-vertical-wizard">
                            {
                                steps.steps.map( (item ,index)=> (
                                    <Step
                                        key={item.id}
                                        id={item.id}
                                        label={item.title}
                                        chronology={item.chronology}
                                        status={item.completed}
                                        current={(item.status== false && steps.steps[index-1].status==true) ? true : false }
                                    />
                                ))
                            }
                        </div>
                    </div>

                    <div className="col-sm-8 col-md-9">
                    </div>

                </div>

</div>

    )
}

export  default Steps
