import { useState, useContext, useEffect } from "react"
import {StepsContext,CurrentContext,SelectedContext }from "./../context"

const  Frame = () => {


    const [steps,setSteps]=useContext(StepsContext)
    const [selectedStep,setSelectedStep]=useContext(SelectedContext)
    const [currentStep,setCurrntStep]=useContext(CurrentContext)
    const [setpFrame,setStepFrame]=useState("")

    const completedHandler =()=>{
           const targetStep = steps.filter(item => item.id= id) 
           targetStep.completed=true
           const newSteps= steps.filter(item => item.id != id)
           setSteps([...newSteps,...targetStep])
    }

    useEffect(() => {
        steps.map((item,index)=>{
            if(item.id===selectedStep){
                if(item.completed){
                    setStepFrame("Completed")
                }else if(item.id == currentStep){
                    setStepFrame("Current")
                }
                else{
                    setStepFrame("Forbidden")
                }
            }
        })
    },)


    if(setpFrame=="Completed"){
        return(
            <div className="border border-dark col-8 d-flex justify-content-center align-item-center">
                <h1>This Step is Completed!</h1>
            </div>
        )
    }else if(setpFrame=="Current"){
            return(
                <div className="border border-dark col-8 d-flex justify-content-center align-item-center">
                    <button className="btn btn-danger align-self-center" onClick={completedHandler}>complete</button>
                </div>
            )
    }else if(setpFrame=="Forbidden"){
            return(
                <div className="border border-dark col-8 d-flex justify-content-center align-item-center">
                    <h1>You must do previous Steps!</h1>
                </div>
            )
    }else{
            return(
                <div className="border border-dark col-8 d-flex justify-content-center align-item-center">

                </div>
            )
       }

}
export  default Frame
