import StepContext from "./../context/StepsContext"
const  Step = ({id,status,label,chronology,current,selected}) => {


    return(
            <div className={`prev-step step
                                ${status ? "complete" :"" } 
                                ${current ? "current" : ""}  
                            `} 
                onClick={()=>selected(id)}
            >
                <div>
                    <i className={`ico fa fa-check ico-green`} ></i>
                    <span className={`text-secondary label
                                        ${ (status || current ) ? "font-weight-bolder text-dark" :"" }
                                        ${ selected ? "shadow" : ""}
                                    `} > 
                    {label}</span>
                </div>
            </div>
        )
}
export  default Step
