import "./steps.css"
import stepsApi from "./../Api/stepsApi"
import {useState} from "react"
import Step from "./Step";

const Steps = () => {

    const [steps,setSteps] = useState(stepsApi)
    const [current ,setCurrent]= useState(false)
    const [select,setSelected] = useState()

    steps.steps.map((item,index)=>{
        if(item.completed===false && steps.steps[index-1].completed===true){
            console.log(steps.steps[index-1].completed);
        }
    })
    return(

                <div className="col-4">    
                    <div className="side-content">
                        <div className="bs-vertical-wizard">
                            {
                                steps.steps.map( (item ,index)=> (
                                    <Step
                                        key={item.id}
                                        id={item.id}
                                        label={item.title}
                                        chronology={item.chronology}
                                        status={item.completed}
                                        current={(item.completed=== false && steps.steps[index-1].completed===true)? true : false}
                                        selected={(id)=>setSelected()}
                                    />
                                ))
                            }
                        </div>
                    </div>

                    <div className="col-sm-8 col-md-9">
                    </div>

                </div>

    )
}

export  default Steps
