import { useState } from "react"

const  Step = ({id,status,label,chronology,current}) => {
    const [select,setSelected] = useState(false)

    return(
            <div className={`prev-step step
                                ${status ? "complete" :"" } 
                                ${current ? "current" : ""}

                            `} 
            onClick={()=>setSelected(true)}
            >
                <div>
                    <i className={`ico fa fa-check ico-green`} ></i>
                    <span className={`text-secondary ${ (status || current ) ? "font-weight-bolder text-dark" :"" }`} > {label}</span>
                </div>
            </div>
        )
}
export  default Step
